transition_duration.css_code:
.squares {
  width: 100px;
  height: 100px;
  border-radius: 8px;
  background-color: springgreen;
  transition-property: transform;
  transition-timing-function: ease;
  padding: 8px;
}
.squares.td-instant {
  transition-duration: 0s;
}
.squares.td-speedy {
  transition-duration: 200ms;
}
.squares.td-middling {
  transition-duration: 800ms;
}
.squares.td-glacial {
  transition-duration: 5s;
}
.squares:is(:hover, :focus) {
  transform: scale(0.8);
}
.wrapper {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 32px;
}
.wrapper .squares:nth-child(odd) {
  justify-self: end;
}
.wrapper .squares:nth-child(even) {
  justify-self: start;
}

transition_timing_function.css_code:
.square {
   width: 150px;
   max-height: 100px;
   background-color: lightcoral;
   border-radius: 8px;
   text-align: center;
   padding: 8px;
   margin-bottom: 4px;
   transition-property: width;
   transition-duration: 1s;
}

.square.ttf-linear {
   transition-timing-function: linear;
}

.square.ttf-ease {
   transition-timing-function: ease;
}

.square.ttf-ease-in {
   transition-timing-function: ease-in;
}
.square.ttf-ease-out {
   transition-timing-function: ease-out;
}

.square.ttf-ease-in-out {
   transition-timing-function: ease-in-out;
}

.square.ttf-steps {
   transition-timing-function: steps(3);
}

.square.ttf-cubic {
   transition-timing-function: cubic-bezier(0.68, -0.55, 0.27, 1.55);
}

.square:is(:hover, :focus) {
   width: 50%;
}

transition_delay.css_code:
.rectangle {
    width: 150px;
    max-height: 100px;
    border-radius: 8px;
    text-align: right;
    padding: 8px;
    margin-bottom: 4px;
 }

.rectangle:nth-child(1) {
    transition-delay: 0s;
 }

 .rectangle:nth-child(2) {
    transition-delay: 100ms;
 }

 .rectangle:nth-child(3) {
    transition-delay: 200ms;
 }

 .rectangle:nth-child(4) {
    transition-delay: 500ms;
 }

 .rectangle:nth-child(5) {
    transition-delay: 1000ms;
 }

 .rectangle:is(:hover, :focus) {
    background-color: transparent;
 }
